mapper.select:
SELECT ca.amount AS amount, cu.`name` AS salesmanName, cu.id AS salesmanId, cu.phone AS salesmanPhone, ca.count AS salesmanCount FROM ( SELECT ck.salesman_no, SUM( ck.amount ) AS amount, SUM( ck.count ) AS count FROM ck_order ck LEFT OUTER JOIN ck_product cp ON cp.id = ck.product_id
ck.status = 1
AND ck.`year_month` = #{yearMonth,jdbcType=VARCHAR}
AND ck.`year` = #{yearMonth,jdbcType=VARCHAR}
AND cp.product_type = #{productType,jdbcType=VARCHAR}
GROUP BY ck.salesman_no ) ca LEFT OUTER JOIN ck_user cu ON cu.id = ca.salesman_no ORDER BY ca.amount DESC, ca.count DESC

SELECT cp.id AS productId, cp.`name` AS productName, cc.count AS productCount FROM ( SELECT co.product_id, SUM( co.count ) AS count FROM ck_order co LEFT OUTER JOIN ck_product cp ON cp.id = co.product_id
co.status = 1
AND co.`year_month` = #{yearMonth,jdbcType=VARCHAR}
AND co.`year` = #{yearMonth,jdbcType=VARCHAR}
AND cp.product_type = #{productType,jdbcType=VARCHAR}
GROUP BY co.product_id ) cc LEFT OUTER JOIN ck_product cp ON cp.id = cc.product_id ORDER BY cc.count DESC, cc.product_id ASC

SELECT cu.id AS salesmanNo, cu.`name` AS salesmanName, cu.phone AS salesmanPhone, SUM( co.amount ) AS `amount`, SUM( co.count ) AS `count`, cu.`status` AS salesmanStatus, cd.`name` AS salesmanDistrictname FROM ck_order co LEFT JOIN ck_user cu ON cu.id = co.salesman_no LEFT JOIN ck_district cd ON cu.district_no = cd.id
AND co.`year_month` = #{yearMonth,jdbcType=BIGINT}
AND co.`year` = #{yearMonth,jdbcType=BIGINT}
AND co.product_id=#{productId,jdbcType=BIGINT}
GROUP BY co.salesman_no ORDER BY `amount` DESC, `count` DESC

SELECT co.product_id AS productId, SUM( co.amount ) AS `amount`, SUM( co.count ) AS `count`, cp.`name` AS productName, cp.`status` AS productStatus, cpt.`name` AS productTypeName FROM ck_order co LEFT OUTER JOIN ck_product cp ON cp.id = co.product_id LEFT OUTER JOIN ck_product_type cpt ON cpt.`code` = cp.product_type
AND co.`year_month` = #{yearMonth,jdbcType=BIGINT}
AND co.`year` = #{yearMonth,jdbcType=BIGINT}
AND co.salesman_no=#{salesmanNo,jdbcType=BIGINT}
GROUP BY co.product_id , cpt.`name` ORDER BY `amount` DESC, `count` DESC
